mapper.update:
UPDATE role_menu_action rma
SET rma.action_ids = #{ids}
WHERE rma.role_id = #{role_id}
  AND rma.menu_id = (
        SELECT m.id
        FROM menu m
        WHERE m.`key` = #{key}
        )

mapper.select:
select *
from role_menu_action rma
where rma.role_id = #{role_id}
  and rma.menu_id = (
        select m.id
        from menu m
        where m.`key` = #{key}
        )
